pragma solidity 0.8.15;

import { ERC721K } from "@erc721k/core-sol/contracts/ERC721K.sol";
import { ERC721Storage } from "@erc721k/core-sol/contracts/ERC721Storage.sol";
import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";
import { LicenseVersion, CantBeEvil } from "@a16z/contracts/licenses/CantBeEvil.sol";
import { ISVGRender } from "@erc721k/periphery-sol/contracts/interfaces/ISVGRender.sol";
import { IPixelCharacter } from "./interfaces/IPixelCharacter.sol";
import { PixelPoolyStorage } from "./PixelPoolyStorage.sol";
import { PixelStore } from "./PixelStore.sol";

interface IERC721StorageSync {
  function initialize(uint256 _tokenId) external;

  function validate(uint256 _tokenId) external;

  function slots(uint256 _tokenId) external view returns (uint8[7] memory);

  function traits(uint256 _tokenId) external view returns (uint8[7] memory);
}

/**
 * @title PixelPooly
 * @author Friends of Pooly, Inc.
 * @notice A PixelPooly Collectable using the ERC721K framework and Can't Be Evil license.
 */
contract PixelPooly is IPixelCharacter, ERC721K, AccessControl, CantBeEvil {
  bytes32 public constant MINTER_BURNER_ROLE = keccak256("MINTER_BURNER_ROLE");

  /// -----------------------------------------------------------------------
  /// Constructor
  /// -----------------------------------------------------------------------

  /**
   * @notice PixelPooly Construction
   * @param name string - Name of ERC721 token
   * @param symbol string - Symbol of ERC721 token
   * @param erc721Storage address - ERC721Storage instance
   */
  constructor(
    string memory name,
    string memory symbol,
    address erc721Storage
  ) ERC721K(name, symbol, erc721Storage) CantBeEvil(LicenseVersion.CBE_NECR_HS) {}

  /// -----------------------------------------------------------------------
  /* EIP Functions                                                                         */
  /// -----------------------------------------------------------------------
  function supportsInterface(bytes4 interfaceId)
    public
    view
    virtual
    override(CantBeEvil, ERC721K, AccessControl)
    returns (bool)
  {
    return super.supportsInterface(interfaceId);
  }

  function ownerOf(uint256 tokenId) public view virtual override returns (address) {
    address owner = _ownerOf[tokenId];
    if (owner == address(0)) {
      return address(0);
    }
    return owner;
  }

  /// -----------------------------------------------------------------------
  /// External Functions
  /// -----------------------------------------------------------------------

  // --------------------------------------
  // READS
  // --------------------------------------

  function preview(address account) external view returns (string memory) {
    bytes memory imageBytes = PixelPoolyStorage(_erc721Storage).getPreview(0, account);
    return ISVGRender(ERC721Storage(_erc721Storage).getSvgRender()).render(imageBytes);
  }

  // --------------------------------------
  // WRITES
  // --------------------------------------

  /**
   * @notice Mints a new token to the given address
   * @param to address - Address to mint to`
   */
  function mint(address to) external returns (uint256) {
    require(hasRole(MINTER_BURNER_ROLE, _msgSender()), "Web3Card:unauthorized");
    uint256 nextId = _idCounter++;
    _mint(to, nextId);
    return nextId;
  }

  /**
   * @notice Burns a token
   * @param tokenId uint256 - Token ID to burn
   */
  function burn(uint256 tokenId) external {
    require(hasRole(MINTER_BURNER_ROLE, _msgSender()), "Web3Card:unauthorized");
    _burn(tokenId);
  }

  function equip(
    uint256 tokenId,
    uint8 slot,
    uint8 position
  ) external {
    require(_msgSender() == ownerOf(tokenId), "PixelPooly:unauthorized");
    PixelPoolyStorage(_erc721Storage).setPositionSlot(tokenId, slot, position);
  }

  function buy(
    uint256 tokenId,
    uint8[] calldata position,
    uint8[] calldata slot
  ) external payable {
    _unlockInStore(position, slot);
  }

  /// -----------------------------------------------------------------------
  /// Internal Functions
  /// -----------------------------------------------------------------------

  function _unlockInStore(uint8[] calldata position, uint8[] calldata slot)
    internal
    returns (uint8)
  {
    PixelStore(PixelPoolyStorage(_erc721Storage).getStore()).unlock{ value: msg.value }(
      position,
      slot
    );
  }

  function _tokenData(uint256 _tokenId)
    internal
    view
    virtual
    override
    returns (bytes memory, bytes memory)
  {
    // Expects position/slot data for every image layer (7)
    bytes memory imageBytes = PixelPoolyStorage(_erc721Storage).getImageBytes(_tokenId);
    // Expects address of the token owner
    bytes memory traitsBytes = PixelPoolyStorage(_erc721Storage).getTraitsBytes(_tokenId);
    return (imageBytes, traitsBytes);
  }
}

