// SPDX-License-Identifier: MIT
pragma solidity 0.8.15;

import { Base64 } from "base64-sol/base64.sol";
import { IERC721 } from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import { Strings } from "@openzeppelin/contracts/utils/Strings.sol";
import { ERC721Storage } from "@erc721k/core-sol/contracts/ERC721Storage.sol";
import { IERC721KImage } from "@erc721k/core-sol/contracts/interfaces/IERC721KImage.sol";
import { IERC721KTraits } from "@erc721k/core-sol/contracts/interfaces/IERC721KTraits.sol";
import { IPixelCharacter } from "./interfaces/IPixelCharacter.sol";
import { PixelStore } from "./PixelStore.sol";

/**
 * @title PixelPoolyStorage
 * @author Kames Geraghty
 */
contract PixelPoolyStorage is ERC721Storage {
  address public erc721Instance;
  address public pixelStoreInstance;
  address public erc721ImageSignal;
  bool private isSignalActive;

  /// @notice The active item slots for a Pixel Pooly character.
  mapping(uint256 => Character) private _characters;

  /// @notice Track unlocked item slots using a bytes32 (hash of item type and slot)
  mapping(uint256 => mapping(bytes32 => bool)) private _items;

  struct Character {
    uint8 background;
    uint8 head;
    uint8 body;
    uint8 headAcc;
    uint8 bodyAcc;
    uint8 leftAcc;
    uint8 rightAcc;
  }

  /// -----------------------------------------------------------------------
  /// Constructor
  /// -----------------------------------------------------------------------

  constructor(
    address _svgRender_,
    address _traitsFetch_,
    ContractURI memory _contractURI_
  ) ERC721Storage(_svgRender_, _traitsFetch_, _contractURI_) {}

  /// -----------------------------------------------------------------------
  /// Overrides & EIP Functions
  /// -----------------------------------------------------------------------
  function constructTokenURI(
    uint256 tokenId,
    bytes memory input0,
    bytes memory input1
  ) external view override returns (string memory uri) {
    if (isSignalActive) {
      return _signalRender(tokenId);
    } else {
      string memory image_ = IERC721KImage(_svgRender).render(input0);
      string memory traits_ = IERC721KTraits(_traitsFetch).fetch(input1);
      return
        string(
          abi.encodePacked(
            "data:application/json;base64,",
            Base64.encode(
              bytes(
                string.concat(
                  '{"name":',
                  '"',
                  _parseName(tokenId),
                  '",',
                  '"description":',
                  '"',
                  _parseDescription(tokenId),
                  '",',
                  '"image":',
                  '"',
                  image_,
                  '",',
                  '"attributes": [',
                  traits_,
                  "]",
                  "}"
                )
              )
            )
          )
        );
    }
  }

  /// -----------------------------------------------------------------------
  /// External Functions
  /// -----------------------------------------------------------------------

  // ========================
  // READS
  // ========================

  function getCharacter(uint256 tokenId) external view returns (Character memory) {
    return _characters[tokenId];
  }

  function getStore() external view returns (address) {
    return pixelStoreInstance;
  }

  /**
   * @notice Get the active item slots for a Pixel Pooly character
   * @param tokenId uint256 - The token ID to query
   * @return bytes - Packed bytes of the active item slots
   */
  function getPreview(uint256 tokenId, address account) external view returns (bytes memory) {
    return _imageBytes(tokenId, account);
  }

  /**
   * @notice Get the active item slots for a Pixel Pooly character
   * @param tokenId uint256 - The token ID to query
   * @return bytes - Packed bytes of the active item slots
   */
  function getImageBytes(uint256 tokenId) external view returns (bytes memory) {
    address account = IERC721(erc721Instance).ownerOf(tokenId);
    return _imageBytes(tokenId, account);
  }

  function _imageBytes(uint256 tokenId, address account) internal view returns (bytes memory) {
    Character memory character = _characters[tokenId];
    return
      bytes(
        abi.encode(
          character.background,
          character.head,
          character.body,
          character.headAcc,
          character.bodyAcc,
          character.leftAcc,
          character.rightAcc
        )
      );
  }

  /**
   * @notice Get the trait bytes for a Pixel Pooly character
   * @param tokenId uint256 - The token ID to query.
   * @return bytes - Packed instructions for traits PixelPoolyTraits
   */
  function getTraitsBytes(uint256 tokenId) external view returns (bytes memory) {
    return bytes(abi.encode(IERC721(erc721Instance).ownerOf(tokenId)));
  }

  function isItemSlotActive(
    uint256 tokenId,
    uint8 position,
    uint8 slot
  ) external view returns (bool) {
    bytes32 slotHash = keccak256(abi.encodePacked(position, slot));
    return _items[tokenId][slotHash];
  }

  // ========================
  // WRITES
  // ========================

  function setERC721KInstance(address _erc721Instance) external onlyOwner {
    erc721Instance = _erc721Instance;
  }

  function setPixelStoreInstance(address _pixelStoreInstance) external onlyOwner {
    pixelStoreInstance = _pixelStoreInstance;
  }

  function setPositionSlot(
    uint256 tokenId,
    uint8 position,
    uint8 slot
  ) external {
    require(_msgSender() == erc721Instance, "PixelPoolyStorage:only-erc721");
    bytes32 slotHash = keccak256(abi.encodePacked(position, slot));
    require(_items[tokenId][slotHash], "PixelPoolyStorage:slot-inactive");
    if (position == 0) {
      _characters[tokenId].background = slot;
    } else if (position == 1) {
      _characters[tokenId].head = slot;
    } else if (position == 2) {
      _characters[tokenId].body = slot;
    } else if (position == 3) {
      _characters[tokenId].headAcc = slot;
    } else if (position == 4) {
      _characters[tokenId].bodyAcc = slot;
    } else if (position == 5) {
      _characters[tokenId].leftAcc = slot;
    } else if (position == 6) {
      _characters[tokenId].rightAcc = slot;
    }
  }

  /// -----------------------------------------------------------------------
  /// Internal Functions
  /// -----------------------------------------------------------------------

  function _parseName(uint256 _tokenId) internal view override returns (string memory) {
    return string.concat("PixelPooly #", Strings.toString(_tokenId));
  }

  function _parseDescription(uint256 _tokenId) internal view override returns (string memory) {
    return string.concat("Member #", Strings.toString(_tokenId), " of PixelPooly.");
  }

  function _signalRender(uint256 tokenId) internal view returns (string memory uri) {
    bytes memory _empty = bytes("");
    string memory image_ = IERC721KImage(erc721ImageSignal).render(_empty);
    return
      string(
        abi.encodePacked(
          "data:application/json;base64,",
          Base64.encode(
            bytes(
              string.concat(
                '{"name":',
                '"',
                _parseName(tokenId),
                '",',
                '"description":',
                '"',
                _parseDescription(tokenId),
                '",',
                '"image":',
                '"',
                image_,
                '",',
                '"attributes": [',
                "]",
                "}"
              )
            )
          )
        )
      );
  }
}

