// SPDX-License-Identifier: MIT
pragma solidity 0.8.15;

import { OwnedRoles } from "@turbo-eth/solbase-sol/src/auth/OwnedRoles.sol";

contract PixelStore is OwnedRoles {
  enum Tiers {
    COMMON,
    RARE,
    EPIC,
    LEGENDARY
  }

  struct Trait {
    bytes32 id;
    uint8 position;
    uint8 slot;
    string traitName;
    string traitValue;
    Tiers tier;
    uint256 expiry;
  }

  /// -----------------------------------------------------------------------
  /// Storage
  /// -----------------------------------------------------------------------
  address internal erc721K;
  address internal erc721Storage;
  mapping(Tiers => uint256) internal tierPrices;
  mapping(bytes32 => bool) internal _isUnlocked;
  mapping(bytes32 => Trait) internal pixels;

  /// -----------------------------------------------------------------------
  /// Constructor & Modifiers
  /// -----------------------------------------------------------------------

  constructor(address _owner) OwnedRoles() {
    _initializeOwner(_owner);
  }

  /// -----------------------------------------------------------------------
  /// Internal Functions
  /// -----------------------------------------------------------------------

  /// -----------------------------------------------------------------------
  /// External Functions
  /// -----------------------------------------------------------------------

  // ========================
  // READS
  // ========================
  function isUnlocked(
    address token,
    uint256 tokenId,
    uint8 position,
    uint8 slot
  ) external view returns (bool unlocked) {
    bytes32 id = keccak256(abi.encodePacked(token, position, slot));
    return _isUnlocked[id];
  }

  // ========================
  // WRITES
  // ========================
  function add(
    uint8 position,
    uint8 slot,
    string memory traitName,
    string memory traitValue,
    Tiers tier,
    uint256 expiry
  ) external onlyOwner {
    bytes32 id = keccak256(abi.encodePacked(msg.sender, position, slot));
    Trait memory trait = Trait(id, position, slot, traitName, traitValue, tier, expiry);
    pixels[id] = trait;
  }

  function unlock(uint8[] calldata position, uint8[] calldata slot) external payable {
    require(position.length == slot.length, "PixelStore:invalid-length");
    uint256 cost;
    for (uint256 index = 0; index < position.length; index++) {
      bytes32 id = keccak256(abi.encodePacked(msg.sender, position[index], slot[index]));
      Trait memory trait = pixels[id];
      require(!_isUnlocked[id], "PixelStore:pixel-unlocked");
      cost += tierPrices[trait.tier];
    }
    require(msg.value >= cost, "PixelStore:insufficient-amount");
  }
}

